package com.swmansion.rnscreens

import android.content.Intent
import android.util.Log
import com.facebook.react.bridge.ReactApplicationContext
import com.facebook.react.bridge.ReactContextBaseJavaModule
import com.facebook.react.bridge.ReactMethod

class IntegrationModule(reactContext: ReactApplicationContext): ReactContextBaseJavaModule(reactContext) {

    override fun getName(): String = "IntegrationModule"

    //TODO: add params
    @ReactMethod
    fun openDetailScreen(){
        Log.d("IntegrationModule", "open detail activity")
        val context = reactApplicationContext
        val intent = Intent(context, MovieDetailActivity::class.java)
        intent.putExtra("title", "Movie name")
        intent.flags = Intent.FLAG_ACTIVITY_NEW_TASK
        context.startActivity(intent)
    }
}